mapper.select:

  	SELECT *
  		FROM result


					AND result_code LIKE #{content}


            		AND done_date BETWEEN #{done_dateBegin} AND #{done_dateEnd}


            		AND done_date >= #{done_dateBegin}

mapper.update:
		UPDATE result
 			SET work_code = #{work_code}, done_date = #{done_date}, line_code = #{line_code}, prod_code = #{prod_code}, good_prod = #{good_prod}, faulty_prod = #{faulty_prod}, faulty_reason = #{faulty_reason}, remark = #{remark}
 				WHERE result_code = #{result_code}

mapper.delete:
DELETE FROM result
	WHERE result_code IN

			#{result_code}
