mapper.update:
UPDATE in_material
SET in_code = #{in_code}, in_wh_code = #{in_wh_code}, raw_order_code = #{raw_order_code},
    emp_num = #{emp_num}, in_status = #{in_status}
WHERE in_code = #{in_code}

mapper.select:

 		SELECT *
			FROM(
				SELECT in_code, in_wh_code, raw_order_code as raw_order_code, emp_num, in_status
  					FROM in_material
				) AS myTable
					WHERE myTable.${category} LIKE #{content}

SELECT buy_code
FROM buy
WHERE buy_name = #{buy_code}
